import React, { useState, useEffect } from 'react';
import DataTable from './components/DataTable';
import AddForm from './components/AddForm';
import EditForm from './components/EditForm';

function App() {
  const [data, setData] = useState([]);
  const [editing, setEditing] = useState(false);
  const [currentData, setCurrentData] = useState({ id: null, title: '', creationTime: '' });

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('data')) || [];
    setData(storedData);
  }, []);

  useEffect(() => {
    localStorage.setItem('data', JSON.stringify(data));
  }, [data]);

  const addData = (newData) => {
    setData([...data, newData]);
  };

  const deleteData = (id) => {
    setData(data.filter(item => item.id !== id));
  };

  const editData = (updatedData) => {
    setData(data.map(item => (item.id === updatedData.id ? updatedData : item)));
    setEditing(false);
  };

  const editRow = (data) => {
    setEditing(true);
    setCurrentData({ id: data.id, title: data.title, creationTime: data.creationTime });
  };

  return (
    <div className="App">
      {editing ? (
        <EditForm currentData={currentData} editData={editData} setEditing={setEditing} />
      ) : (
        <AddForm addData={addData} />
      )}
      <DataTable data={data} deleteData={deleteData} editRow={editRow} />
    </div>
  );
}

export default App;
